from rest_framework import serializersfrom .models import BookInfo# class BookInfoSerializer(serializers.ModelSerializer):#     """定义序列化器"""#     class Meta:#         model = BookInfo  # 指定序列化从那个模型映射字段#         fields = '__all__'  # 映射那些字段class BookInfoSerializer(serializers.Serializer):    """书籍的序列化器"""    id = serializers.IntegerField(label='ID', read_only=True)    btitle = serializers.CharField(max_length=20, label='名称', required=True)    bpub_date = serializers.DateField(label='发布日期')    bread = serializers.IntegerField(label='阅读量', required=False)    bcomment = serializers.IntegerField(label='评论量', required=False)    is_delete = serializers.BooleanField(label='逻辑删除', required=False)"""book 模型中有7个属性book.aa = 10{8个key: value}BookInfoSerializer(instace, data)如果直给instace 形参传递参数表示做序列化serializer = BookInfoSerializer(instace = book)serializer.data  获取到序列化后的数据"""